{"windows":"// Place your key bindings in this file to override the defaultsauto[]
[
	{
		"key": "ctrl+alt+s",
		"command": "workbench.action.files.saveLocalFile",
		"when": "remoteFileDialogVisible"
	},
	{
		"key": "ctrl+shift+s",
		"command": "-workbench.action.files.saveLocalFile",
		"when": "remoteFileDialogVisible"
	},
	{
		"key": "ctrl+shift+alt+s",
		"command": "workbench.action.files.saveAs"
	},
	{
		"key": "ctrl+shift+s",
		"command": "-workbench.action.files.saveAs"
	},
	{
		"key": "ctrl+shift+s",
		"command": "workbench.action.files.saveAll"
	},
	{
		"key": "ctrl+k s",
		"command": "-workbench.action.files.saveAll"
	},
	{
		"key": "ctrl+d",
		"command": "editor.action.copyLinesDownAction",
		"when": "editorTextFocus && !editorReadonly"
	},
	{
		"key": "shift+alt+down",
		"command": "-editor.action.copyLinesDownAction",
		"when": "editorTextFocus && !editorReadonly"
	},
	{
		"key": "ctrl+f5",
		"command": "workbench.action.debug.run",
		"when": "debuggersAvailable && debugState != 'initializing'"
	},
	{
		"key": "ctrl+f5",
		"command": "-workbench.action.debug.run",
		"when": "debuggersAvailable && debugState != 'initializing'"
	},
	{
		"key": "f5",
		"command": "workbench.action.debug.start",
		"when": "debuggersAvailable && debugState == 'inactive'"
	},
	{
		"key": "f5",
		"command": "-workbench.action.debug.start",
		"when": "debuggersAvailable && debugState == 'inactive'"
	},
	{
		"key": "ctrl+shift+f5",
		"command": "code-runner.run"
	},
	{
		"key": "ctrl+alt+n",
		"command": "-code-runner.run",
	},
	{
		"key": "f5",
		"command": "workbench.action.debug.continue",
		"when": "debugState == 'stopped'"
	},
	{
		"key": "f5",
		"command": "-workbench.action.debug.continue",
		"when": "debugState == 'stopped'"
	},
	{
		"key": "ctrl+shift+f6",
		"command": "debug.openView",
		"when": "!debuggersAvailable"
	},
	{
		"key": "f5",
		"command": "-debug.openView",
		"when": "!debuggersAvailable"
   },
   {
      "key": "ctrl+f5",
      "command": "C_Cpp.BuildAndDebugActiveFile"
   },
   {
      "key": "ctrl+f5",
      "command": "java.debug.runJavaFile"
	},
	{
		"key": "ctrl+f5",
		"command": "java.debug.debugJavaFile"
	},
	{
		"key": "ctrl+f5",
		"command": "quokka.playTraceForwardToSelection",
		"when": "activeEditor && quokka.isActiveEditorRunningQuokka && quokka.traceBeingNavigated && !inDebugMode && !quokka.isLiveShareClient || activeEditor && quokka.traceBeingNavigated && !inDebugMode && !quokka.isLiveShareClient && resourceScheme == 'quokka-code-timeline'"
	},
	{
		"key": "f5",
		"command": "-quokka.playTraceForwardToSelection",
		"when": "activeEditor && quokka.isActiveEditorRunningQuokka && quokka.traceBeingNavigated && !inDebugMode && !quokka.isLiveShareClient || activeEditor && quokka.traceBeingNavigated && !inDebugMode && !quokka.isLiveShareClient && resourceScheme == 'quokka-code-timeline'"
	}
]"}
