{"windows":"// Place your key bindings in this file to override the defaultsauto[]
[
    //>> ** MODIFIED BY ME **
  
  {
    "key": "space f",
    "command": "workbench.action.quickOpen",
    "when":"filesExplorerFocus && !terminalFocus"
  },
  {
    "key": "alt+i",
    "command": "workbench.action.terminal.toggleTerminal",    
    "when": "terminal.active"
  },
  {
    "key": "ctrl+oem_3",
    "command": "-workbench.action.terminal.toggleTerminal",
    "when": "terminal.active"
  },
  {
    "key":     "alt+i",
    "command": "workbench.action.terminal.focus",
    "when": "!terminal.active"
  },
  {
      "key":     "alt+i",
      "command": "-workbench.action.focusActiveEditorGroup",
      "when":    "terminalFocus"
  },
  {
    "key": "ctrl+up",
    "command": "cursorMove",
    "args": {
        "to": "prevBlankLine",
        // "select": true
    },
    "when": "editorTextFocus"
  },
  {
    "key": "ctrl+down",
    "command": "cursorMove",
    "args": {
        "to": "nextBlankLine",
        // "select": true
    },
  },
  {
    "key": "space e",
    "command": "workbench.view.explorer",
    "when": "!editorTextFocus && !terminalFocus"
  },
  {
    "key": "space c",
    "command": "workbench.action.toggleSidebarVisibility",
    "when": "!editorTextFocus && !terminalFocus"
  },
  {
    "key": "ctrl+shift+oem_3",
    "command": "workbench.action.toggleActivityBarVisibility"
  },
  {
    "key": "ctrl+p",
    "command": "-workbench.action.quickOpen"
  },
  {
    "key": "ctrl+shift+t",
    "command": "-workbench.action.reopenClosedEditor"
  },
  {
    "key": "d",
    "command": "deleteFile",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus && !terminalFocus"
  },
  {
    "key": "a",
    "command": "explorer.newFile",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus && !terminalFocus"
  },
  {
    "key": "m f",
    "command": "explorer.newFile",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus && !terminalFocus"
  },
  {
    "key": "c",
    "command": "filesExplorer.copy",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus && !terminalFocus"
    //"when": "explorerViewletVisible && filesExplorerFocus && !terminalFocus && !explorerResourceMoveableToTrash && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "p",
    "command": "filesExplorer.paste",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus && !terminalFocus"
    //"when": "explorerViewletVisible && filesExplorerFocus && !terminalFocus && !explorerResourceMoveableToTrash && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "s",
    "command": "revealFileInOS",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus && !terminalFocus"
    //"when": "explorerViewletVisible && filesExplorerFocus && !terminalFocus && !explorerResourceMoveableToTrash && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "m d",
    "command": "explorer.newFolder",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus && !terminalFocus"
    
    //"when": "explorerViewletVisible && filesExplorerFocus && !terminalFocus && !explorerResourceMoveableToTrash && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "ctrl+k r",
    "command": "-workbench.action.files.revealActiveFileInWindows"
  },
  {
    "key": "shift+alt+r",
    "command": "-java.view.package.revealFileInOS",
    "when": "focusedView == 'javaProjectExplorer' && java:serverMode == 'Standard'"
  },
  {
    "key": "ctrl+k f12",
    "command": "-editor.action.revealDefinitionAside",
    "when": "editorHasDefinitionProvider && editorTextFocus && !isInEmbeddedEditor"
  },
  {
    "key": "ctrl+k e",
    "command": "-workbench.files.action.focusOpenEditorsView",
    "when": "workbench.explorer.openEditorsView.active"
  },
  {
    "key": "shift+e",
    "command": ""
  },
  {
    "key": "r",
    "command": "renameFile",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus"
  },
  {
    "key": "f2",
    "command": "-renameFile",
    "when": "explorerViewletVisible && filesExplorerFocus && !explorerResourceIsRoot && !explorerResourceReadonly && !inputFocus"
  }
]"}
